import React from 'react';
import './Project.css';
import breweryfinder from '../images/brewery-finder.png';
import passwordgenerator from '../images/password-generator.PNG';
import queersource from '../images/queer-source.PNG';
import socialnetworkapi from '../images/social-network-api.PNG';
import techblog from '../images/tech-blog.PNG';
import weatherdashboard from '../images/weather-dashboard.PNG';

const imgarr = [breweryfinder, passwordgenerator, queersource, socialnetworkapi, techblog, weatherdashboard];

export default function Project(props) {
  return (
    <div class="row projects">
        {props.projects.map(project => (
            <div class="card bg-dark text-white col-lg-6 col-sm-12 col-md-6 p-5" id="frames">
            <img src={imgarr} class="card-img" alt="Screenshot of a project with a descriptive caption overlay." />
            <div class="card-img-overlay">
              <h5 class="card-title">{project.name}</h5>
              <p class="card-text">{project.description}</p>
            </div>
          </div>
        ))}
    </div>
  );
};
